const openBtn=document.querySelector(".openBtn"),modal=document.querySelector(".js-modal"),loginModal=document.querySelector(".js-lkAuthed");openBtn?.addEventListener("click",(e=>{modal?.classList.add("show"),loginModal?.classList.add("show")}));const closeBtns=document.querySelectorAll(".js-closeBtn");closeBtns?.forEach((e=>{e.addEventListener("click",(()=>{modal?.classList.remove("show"),loginModal?.classList.remove("show")}))}));