document.querySelector(".showMore")?.addEventListener("click",(e=>{const n=document.querySelector(".aboutMore"),s=document.querySelector(".linkIcon");console.log(s);const c=n?.classList.contains("text-visible");if(c)return n.classList.remove("text-visible"),s.classList.remove("linkIcon-close"),s.classList.add("linkIcon-open"),e.currentTarget.innerHTML="Узнать больше <span class='linkIcon linkIcon-open'></span>",!1;n.classList.add("text-visible"),s.classList.add("linkIcon-close"),s.classList.remove("linkIcon-open"),e.currentTarget.innerHTML="Свернуть <span class='linkIcon linkIcon-close'></span>"}));